    private fun parseRawToJson(rawValue: String): JSONArray {

        val splitted = rawValue.split(",")
        var array = JSONArray()
        try {

            splitted.forEach {
                if(it.contains("invoice")){

                }
                val keyValue = it.split(":")
                val jsonObject = JSONObject()
                if (keyValue.size > 2) {
                    val key = keyValue[0]
                    val value = keyValue[1]
                    val value2 = keyValue[2]
                    jsonObject.put("name", key)
                    jsonObject.put("value", value+":"+value2)
                } else if (keyValue.size > 1) {
                    val key = keyValue[0]
                    val value = keyValue[1]
                    jsonObject.put("name", key)
                    jsonObject.put("value", value)
                } else if (keyValue.isNotEmpty()) {
                    val value = keyValue[0]
                    jsonObject.put("name", "Address")
                    jsonObject.put("value", value)
                }
                array.put(jsonObject)
            }
        } catch (e: Exception) {
            e.printStackTrace()
        }

        return array
    }

    private fun parseJSonToRawString(array: JSONArray): String {
        var string = "["
        for (i in 0 until array.length()) {
            try {

                if (i != 0) {
                    string = string + ","
                }
                string = string + "{"
                val objectJson = array.getJSONObject(i)
                var i= 0
                objectJson.keys().forEach {
                    val value = objectJson.get(it)
                    if(i!=0){
                        string = string +","
                    }
                    string = string + "\\\"" + it + "\\\"" + ":" + "\\\"" + value + "\\\""
                    i = i+1
                }
                string = string + "}"
            } catch (e: JSONException) {
                e.printStackTrace()
            }
        }
        string = string + "]"
        return string
    }

